        <Button variant="contained" fullWidth>
          Button
        </Button>
      </div>

      <div className="w-full max-w-md sm:max-w-lg">
        <Button variant="outlined" fullWidth>

        Button
        </Button>
      </div>

      <div className="w-full max-w-md sm:max-w-lg">
        <Button variant="disabled" fullWidth disabled>
          Button

      </Button>
      </div>
    </div>
  );
}
